import StockTable from "./components/StockTable";
import {useEffect, useState} from "react";
import axios from "axios";
import AddStock from "./components/AddStock";
import AverageTable from "./components/AverageTable";


const App =()=> {
    const [stocksData, setStocksData] = useState([])
    const [averageData, setAverageData] = useState([])

    function containsObject(obj, list) {
        for (var i = 0; i < list.length; i++) {
            if (list[i].ticker === obj) {
                return true;
            }
        }
        return false
    }

    const updateAverageData =(stocks) => {
        if(stocks.length>0){
            let numStocks = 0
            let averagePE = 0;
            let averageEnterpriseValue = 0;
            let averageMarketCap = 0;
            let averageRoe = 0;
            let averageEnterpriseToRev = 0;
            let averageEnterpriseToEbitda = 0;
            let averageProfitMargins = 0;
            for(var i=0; i<stocks.length; i++){
                const stock = stocks[i]
                averagePE += stock.forwardPE
                averageEnterpriseToEbitda += stock.enterpriseToEbitda
                averageEnterpriseToRev += stock.enterpriseToRev
                averageMarketCap += stock.marketCap
                averageRoe += stock.roe
                averageEnterpriseValue += stock.enterpriseValue
                averageProfitMargins += stock.profitMargins
                numStocks +=1
            }
            setAverageData([{forwardPE:(averagePE/numStocks),
            enterpriseValue:(averageEnterpriseValue/numStocks),
            enterpriseToRev:(averageEnterpriseToRev/numStocks),
            enterpriseToEbitda:(averageEnterpriseToEbitda/numStocks),
            marketCap:(averageMarketCap/numStocks),
            profitMargins:(averageProfitMargins/numStocks),
            roe:(averageRoe/numStocks)}])
        }else{setAverageData([])}

    }

    useEffect(()=> {
         axios.get("stocks/")
          .then(res => {
              updateAverageData(res.data.stocksData)
              setStocksData(res.data.stocksData)

          }).catch(error => console.error('Error: $(error)'))
    }, [])

    const onAdd =(ticker)=>{
        if(!containsObject(ticker, stocksData)){
            axios.post("stocks/", {ticker:ticker})
                .then(res => {
                updateAverageData([...stocksData, res.data])
                setStocksData([...stocksData, res.data])
                }).catch(function (error) {
                if (error.response) {
                    console.log(error.response)
                }
                })
        }else {alert("Stock already in list!")}

    }

    const onDelete = ({ ticker }) =>{
        axios.delete("delete-stock/" + ticker)
        setStocksData(stocksData.filter((stock) => stock.ticker !== ticker))
        updateAverageData(stocksData.filter((stock) => stock.ticker !== ticker))
    }

    return (
    <div className="App">

        <AddStock onAdd={ onAdd }/>
        {
            (stocksData.length>0) ? <StockTable data={ stocksData } onDelete={ onDelete } />
                : <div>Add a stock stupid</div>
        }
        {
            (averageData.length>0) ? <AverageTable averageData={averageData}/>
                : <></>
        }
    </div>
  );
}

export default App;
