import StockTable from "./components/StockTable";
import {useEffect, useState} from "react";
import axios from "axios";
import AddStock from "./components/AddStock";
import AverageTable from "./components/AverageTable";


const App =()=> {
    const [stocksData, setStocksData] = useState([])
    const [averageData, setAverageData] = useState([])
    const NULL_FIELD_MAGIC_NUMBER = 420.69

    function containsObject(obj, list) {
        for (var i = 0; i < list.length; i++) {
            if (list[i].ticker === obj) {
                return true;
            }
        }
        return false
    }

    const calcAveragePE = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].forwardPE != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].forwardPE
                numStocks+=1
            }
        }
        return total/numStocks
    }
    const calcAverageEV = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].enterpriseValue != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].enterpriseValue
                numStocks+=1
            }
        }
        return total/numStocks
    }

    const calcAverageEvToEbitda = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].enterpriseToEbitda != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].enterpriseToEbitda
                numStocks+=1
            }
        }
        return total/numStocks
    }

    const calcAverageEvToRev = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].enterpriseToRev != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].enterpriseToRev
                numStocks+=1
            }
        }
        return total/numStocks
    }

    const calcAverageRoe = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].roe != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].roe
                numStocks+=1
            }
        }
        return total/numStocks
    }

    const calcAverageProfitMargins = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].profitMargins != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].profitMargins
                numStocks+=1
            }
        }
        return total/numStocks
    }
    const calcAverageMarketCap = (stocks) =>{
        let numStocks = 0
        let total = 0
        for (var i=0;i<stocks.length;i++){
            if(stocks[i].marketCap != NULL_FIELD_MAGIC_NUMBER){
                total+=stocks[i].marketCap
                numStocks+=1
            }
        }
        return total/numStocks
    }


    const updateAverageData =(stocks) => {
        if(stocks.length>0){
            // let numPE, numEV, numEtR, numEtE, numMC, numPM, numROE = 0
            // let totalPE, totalEV, totalEtR, totalEtE, totalMC, totalPM, totalROE = 0
            //
            // for(let i =0; i<stocks.length; i++){
            //     const stock = stocks[i]
            //     if
            // }
            setAverageData([{forwardPE:calcAveragePE(stocks),
            enterpriseValue:calcAverageEV(stocks),
            enterpriseToRev:calcAverageEvToRev(stocks),
            enterpriseToEbitda:calcAverageEvToEbitda(stocks),
            marketCap:calcAverageMarketCap(),
            profitMargins:calcAverageProfitMargins(stocks),
            roe:calcAverageRoe(stocks)}])
        }else{setAverageData([])}

    }

    useEffect(()=> {
         axios.get("stocks/")
          .then(res => {
              updateAverageData(res.data.stocksData)
              setStocksData(res.data.stocksData)

          }).catch(error => console.error('Error: $(error)'))
    }, [])

    const onAdd =(ticker)=>{
        if(!containsObject(ticker, stocksData)){
            axios.post("stocks/", {ticker:ticker})
                .then(res => {
                updateAverageData([...stocksData, res.data])
                setStocksData([...stocksData, res.data])
                }).catch(function (error) {
                if (error.response) {
                    console.log(error.response)
                }
                })
        }else {alert("Stock already in list!")}

    }

    const onDelete = ({ ticker }) =>{
        axios.delete("delete-stock/" + ticker)
        setStocksData(stocksData.filter((stock) => stock.ticker !== ticker))
        updateAverageData(stocksData.filter((stock) => stock.ticker !== ticker))
    }

    return (
    <div className="App">

        <AddStock onAdd={ onAdd }/>
        {
            (stocksData.length>0) ? <StockTable data={ stocksData } onDelete={ onDelete } />
                : <div>Add a stock stupid</div>
        }
        {
            (averageData.length>0) ? <AverageTable averageData={averageData}/>
                : <></>
        }
    </div>
  );
}

export default App;
